import os
import platform

def init():
    global os_name
    os_name = platform.system()
    if os_name == "Windows":
        os.system("color")
    else:
        os.system("setterm -background black -foreground white")
    print(Style.RESET)

def deinit():
    if os_name == "Windows":
        os.system("color")
    else:
        os.system("setterm -background black -foreground white")
    print(Style.RESET)

def clear():
    try:
        if os_name == "Windows":
            os.system("cls")
        else:
            os.system("clear")
    except:
        clear = "\033[2J"
        print(clear)

def reset():
    print(Style.RESET)
    return Style.RESET

def blend(*args, **kwargs):
    values = ''
    for arg in args:
        values += str(arg)

    for key, value in kwargs.items():
        values += str(value)

    return values

def get(color, name='Fore'):
    color_name = color.upper()
    type_name = name.lower().capitalize()
    name_map = {
        'Fore': Fore,
        'Back': Back,
        'Style': Style,
        'Font': Font,
        'BackEffect': BackEffect,
        'Decor': Decor
    }
    name = name_map.get(type_name)
    if hasattr(name, color_name):
        return getattr(name, color_name)
    else:
        return Style.RESET

def gradient(color, parent='Fore'):
    parent_map = {
        'Fore': Fore,
        'Back': Back,
        'Style': Style,
        'Font': Font,
        'BackEffect': BackEffect,
        'Decor': Decor
    }
    parent = parent.lower().capitalize()
    if parent not in parent_map: parent = 'Fore'
    parent = parent_map[parent]
    r_list = []
    for key, value in vars(parent).items():
        if color.upper() in key:
            r_list.append(value)

    return r_list 

def gradientify(text, gradient):
    return_text = ''
    len_gradient = len(gradient) - 1
    len_text = len(text)
    current_index = 0
    for char in range(len_text):
        return_text += gradient[current_index] + text[char]
        current_index += 1
        if current_index > len_gradient:
            current_index = 0

    return return_text

import pygments
from pygments.lexers import get_lexer_by_name
from pygments.formatters import Terminal256Formatter

def code(code, name='text', style='friendly'):
    try:
        lexer = get_lexer_by_name(name)
    except:
        lexer = get_lexer_by_name('text')

    formatter = Terminal256Formatter(style=style.lower())
    return pygments.highlight(code, lexer, formatter)

def new(*, fore='', back='', style=''):
    return blend(fore, back, style)

def color(text, blend):
    return f'{blend}{text}{Style.RESET}'