def shiftcycle(opt, opts: Iterable) -> tuple:
    cnt = len(opts)
    idx = cnt - list(opts).index(opt)
    beg = 1 * cnt - idx
    end = 2 * cnt - idx
    return tuple(islice(cycle(opts), beg, end))
