def checkkws(*attrs, __defaults = None, **kwargs):
    '''Check if any of the attributes are in kwargs, and return the first one found.'''
    val = jointups(argtup(__defaults), nones(len(attrs)), )
    for i, attr in enumerate(attrs):
        if attr not in kwargs: continue
        val = kwargs.get(attr, val[i])
        break
    return val
