def domain(arr): return min(arr), max(arr)

def maxmin(val, *args): return max(val, min(val, *args))

def minmax(val, *args): return min(val, max(val, *args))

def toldif(x, y, tol: Numeric = 0): return abs(x - y) <= tol

# %% ../nbs/01_math.ipynb 17
def nextint(ints: list[int]) -> int:
    ints = sorted(ints)
    for i, curint in enumerate(ints[:-1]):
        nxtint = ints[i + 1]
        if nxtint - curint > 1:
            return curint + 1
    return max(ints) + 1
